import 'package:flutter/material.dart';

class ProductViewPage extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Product View Page'),
      ),
      body: SingleChildScrollView(
        child: Column(
          children: [
            // Image Section
            GestureDetector(
              onScaleUpdate: (ScaleUpdateDetails details) {
                // Handle image resizing here
              },
              child: Container(
                constraints: BoxConstraints(
                  maxHeight: 300,
                  maxWidth: double.infinity,
                ),
                child: Image.network(
                  'data:image/jpeg;base64,', // Replace with your image URL
                  fit: BoxFit.contain,
                ),
              ),
            ),

            // Specifications Section
            Padding(
              padding: const EdgeInsets.all(16.0),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Text('Specifications:',
                      style:
                          TextStyle(fontSize: 20, fontWeight: FontWeight.bold)),
                  SizedBox(height: 10),
                  Text('Price: \$199.99'),
                  Text('Discount: 10%'),
                  Text('Dimensions: 15x10x5 cm'),
                  Text('Type: Electronic'),
                ],
              ),
            ),

            // Comment Section
            Padding(
              padding: const EdgeInsets.all(16.0),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Text('Comments:',
                      style:
                          TextStyle(fontSize: 20, fontWeight: FontWeight.bold)),
                  SizedBox(height: 10),
                  _buildComment('User1', 'This product is amazing!',
                      'data:image/jpeg;base64,'),
                  _buildComment('User2', 'I love this product!',
                      'data:image/jpeg;base64,'),
                  _buildComment('User3', 'Would definitely recommend!',
                      'data:image/jpeg;base64,'),
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }

  Widget _buildComment(String username, String comment, String profilePicUrl) {
    return Padding(
      padding: const EdgeInsets.symmetric(vertical: 5.0),
      child: Row(
        children: [
          CircleAvatar(
            backgroundImage: NetworkImage(profilePicUrl),
            radius: 20,
          ),
          SizedBox(width: 10),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(username, style: TextStyle(fontWeight: FontWeight.bold)),
                Text(comment),
              ],
            ),
          ),
        ],
      ),
    );
  }
}
